#!/bin/bash

# Simple icon generator using base64 encoded PNG
echo "ðŸŽ¨ Generating default MCP Desktop icons..."

mkdir -p assets

# Base64 encoded 16x16 PNG icon (simple blue square with white M)
ICON_16_BASE64=""

# Base64 encoded 256x256 PNG icon
ICON_256_BASE64=""

# Decode and save icons
echo "$ICON_16_BASE64" | base64 -d > assets/tray-icon.png
echo "$ICON_256_BASE64" | base64 -d > assets/icon.png

# Create copies for different sizes
cp assets/icon.png assets/icon@2x.png
cp assets/tray-icon.png assets/tray-icon@2x.png

echo "âœ… Default icons generated in assets/"
echo ""
echo "Generated files:"
echo "  - assets/icon.png (256x256)"
echo "  - assets/icon@2x.png (256x256)"
echo "  - assets/tray-icon.png (16x16)"
echo "  - assets/tray-icon@2x.png (16x16)"
echo ""
echo "For production, you may want to:"
echo "  1. Replace with custom branded icons"
echo "  2. Convert to .ico (Windows) and .icns (macOS)"